sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {

	isDatePast: function(strDate){
	  var rightNow = new GlideDateTime();
	  var isPast = new GlideDateTime(strDate);

		if (isPast.getLocalDate().before(rightNow.getLocalDate())) return true;
		else return false;

	},
	isDateToday: function(strDate){
	 var rightNow = new GlideDateTime();
     var isToday = new GlideDateTime(strDate);
	 var isTodayDate = isToday.getLocalDate();

		if (isTodayDate.compareTo(rightNow.getLocalDate()) == 0) return true;
		else return false;
	},
    type: 'NeedItUtils'
});
