sys_script_include.script:
var GetEmailAdr = Class.create();
GetEmailAdr.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
// Create a methode GetTheEmail. Retrieve it from a record
	// We need sys_id passed from client call script
	// Get parameter sys_parm_UserID from client script
GetTheEmail: function(){
	var userRecord = new GlideRecord('Sys_user');// sys_user table
	var userID = this.getParameter('sysparm_userID');  
	userRecord.get(userID);
	return userRecord.email + '';
},
    type: 'GetEmailAdr'
});
